const handleFirstTabClick=e=>{9===e.keyCode&&(document.body.classList.contains("tabbed")||(document.body.className+=" tabbed"))},handleMenuClick=e=>{document.getElementsByClassName("menu-icon-container")[0].classList.toggle("open"),e.currentTarget.classList.toggle("open")};document.addEventListener("DOMContentLoaded",(e=>{window.onkeydown=handleFirstTabClick,document.getElementsByClassName("menu")[0].onclick=handleMenuClick}));